/* LIQUID HEADER
--------------------------------------------------------------------------------------- */
#Header {position: fixed; z-index: 103; top: 0; right: 0; left: 0; height: 44px; background-color: #faf7f7; box-shadow: inset 0 1px #fff, 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: inset 0 1px #fff, 0 1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: inset 0 1px #fff, 0 1px 3px rgba(34,25,25,0.4);}
/* For when there is a second bar under the header */
#CategoriesBarPage #Header {position: relative; z-index: 105; box-shadow: 0 0 transparent; -moz-box-shadow: 0 0 transparent; -webkit-box-shadow: 0 0 transparent;}
#Pinterest {position: absolute; top: 9px; left: 50%; margin-left: -50px;}

#Search {float: left; margin: 8px 0 0;}
#Search input {float: left; width: 183px; background-color: #faf7f7; border: 1px solid; border-color: #c2c0c0 #cccaca #d1cfcf; box-shadow: 0 1px #fff, inset 0 1px rgba(34,25,25,0.05); -moz-box-shadow: 0 1px #fff, inset 0 1px rgba(34,25,25,0.05); -webkit-box-shadow: 0 1px #fff, inset 0 1px rgba(34,25,25,0.05); font-size: 13px; line-height: 1.3;}
#Search input:focus {background-color: #fff; box-shadow: 0 1px #fff, inset 0 1px rgba(34,25,25,0.1); -moz-box-shadow: 0 1px #fff, inset 0 1px rgba(34,25,25,0.1); -webkit-box-shadow: 0 1px #fff, inset 0 1px rgba(34,25,25,0.1);}
#Search a {float: left; margin-left: -1px; padding: 7px 7px 2px; min-height: 17px; border: 1px solid; border-color: #c2c0c0 #cccaca #d1cfcf; box-shadow: 0 1px rgba(255,255,255,0.9), inset 0 0 2px rgba(255,255,255,0.75); -moz-box-shadow: 0 1px rgba(255,255,255,0.9), inset 0 0 2px rgba(255,255,255,0.75); -webkit-box-shadow: 0 1px rgba(255,255,255,0.9), inset 0 0 2px rgba(255,255,255,0.75);}
#Search a:active {box-shadow: inset 0 1px 1px rgba(34,29,29,0.15); -moz-box-shadow: inset 0 1px 1px rgba(34,29,29,0.15); -webkit-box-shadow: inset 0 1px 1px rgba(34,29,29,0.15);}

.HeaderContainer li {position: relative; display: inline; font-size: 13px;}
.HeaderContainer .nav {position: relative; display: inline-block; color: #524d4d; text-shadow: 0 1px rgba(255,255,255,1);}
.HeaderContainer .nav span {position: absolute; background-position: top center; background-repeat: no-repeat;}
.HeaderContainer .nav:hover span {background-position: center;}
.HeaderContainer .nav:active span {background-position: bottom center;}
.HeaderContainer li:hover .nav {color: #221919;}

.HeaderContainer li ul {position: absolute; display: none; width: 140px; background-color: #fff; border-top: 1px solid #cccaca; box-shadow: 0 2px 4px rgba(34,25,25,0.5); -moz-box-shadow: 0 2px 4px rgba(34,25,25,0.5); -webkit-box-shadow: 0 2px 4px rgba(34,25,25,0.5);}
.HeaderContainer li:hover ul {display: block;}
.HeaderContainer li ul li:first-child a {padding-top: 10px;}
.HeaderContainer li ul li:last-child a {padding-bottom: 10px;}
.HeaderContainer li ul a {display: block; padding: 5px 10px; text-align: left; font-weight: normal; color: #524d4d;}
.HeaderContainer li ul a:hover {color: #221919; text-decoration: none; background-color: #e1dfdf;}
.HeaderContainer li ul a:active {color: #fff; background-color: #cb2027;}

.HeaderContainer .beforeDivider a {padding-bottom: 10px;}
.HeaderContainer .divider a {padding-top: 10px; border-top: 1px solid #e1dfdf;}

/* Navigation Bar */
#Navigation {position: relative; float: right; z-index: 105;}
#Navigation ul {z-index: 105; top: 28px; left: 0;}
#Navigation .nav {height: 29px; padding: 15px 27px 0 14px;}
#Navigation .nav span {top: 20px; right: 14px; width: 7px; height: 6px; background-image: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/DownArrowGray7.png);}
#Navigation .nav .PlusIcon {top: 19px; right: 13px; width: 6px; height: 8px; background-image: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/PlusIconSimple.png);}
#Navigation .LoginNav {padding-right: 14px;}
#Navigation li:hover .nav {text-decoration: none; text-shadow: 0 1px rgba(255,255,255,0.3); background-color: #e1dfdf;}
#Navigation li:active .nav {color: #fff; text-shadow: 0 -1px rgba(34,25,25,0.3); background-color: #cb2027;}
#Navigation #UserNav .nav {padding-left: 38px;}
#Navigation #UserNav img {position: absolute; top: 12px; left: 10px; width: 22px; height: 22px; border-radius: 1px; -moz-border-radius: 1px; -webkit-border-radius: 1px; box-shadow: 0 1px 2px rgba(34,25,25,0.3); -moz-box-shadow: 0 1px 2px rgba(34,25,25,0.3); -webkit-box-shadow: 0 1px 2px rgba(34,25,25,0.3);}
#Navigation #UserNav ul {left: auto; right: 0;}

/* Categories Bar */
#CategoriesBar {position: absolute; z-index: 101; top: 44px; right: 0; left: 0; text-align: center; font-size: 13px; color: #8c7e7e; background-color: #faf7f7; border-top: 1px solid #cfcaca; box-shadow: inset 0 1px #fff, 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: inset 0 1px #fff, 0 1px 3px rgba(34,25,25,0.4); -webkit-box-shadow: inset 0 1px #fff, 0 1px 3px rgba(34,25,25,0.4);}
#CategoriesBar.fixed {position: fixed; top: -1px; right: 0; left: 0;}
#CategoriesBar li ul {z-index: 1; top: 23px; left: -8px;}
#CategoriesBar .nav {height: 23px; padding: 9px 0 0; font-weight: normal;}
#CategoriesBar .selected {font-weight: bold;}
#CategoriesBar .nav span {top: 14px; right: 0; width: 5px; height: 3px; background-image: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/DownArrowGray.png);}
#CategoriesBar .submenu .nav {position: relative; padding-right: 11px}

#InvitesButton {float: right; margin-top: 5px; padding: 4px 10px;}
#FindButton {float: left; margin-top: 5px; padding: 4px 10px; visibility: hidden;}

/* Categories Dropdown */
#CategoriesDropdown {width: 280px;}
#CategoriesDropdown .SubmenuColumn {float: left; width: 140px;}
#CategoriesDropdown .SubmenuColumn a {padding: 5px 10px;}
#CategoriesDropdown .SubmenuColumn a:first-child {padding-top: 10px;}
#CategoriesDropdown .SubmenuColumn a:last-child {padding-bottom: 10px;}



/* NAG
--------------------------------------------------------------------------------------- */
.Nag {position: fixed; z-index: 4; top: 42px; left: 50%; width: 750px; margin-left: -375px; text-align: center; font-size: 18px; -moz-transition: all 1.3s ease-in-out; -webkit-transition: all 1.3s ease-in-out;}
.Nag .Sheet {position: absolute; padding: 20px 15px 15px; background-color: #fff9b1; border: 1px solid rgba(34,25,25,0.25); overflow: hidden; box-shadow: 0 1px 3px rgba(34,25,25,0.2); -moz-box-shadow: 0 1px 3px rgba(34,25,25,0.2); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.2);}
.Nag p {margin: 0; color: #474531; text-shadow: 0 1px rgba(255,255,255,0.5);}
.Nag select {margin-left: 6px;}
.Nag .Sheet1 {position: relative; z-index: 6; bottom: auto !important; background: -moz-linear-gradient(center bottom, #fffcd1 0%, #fff9b1 40%, #fff9b1 100%); background: -o-linear-gradient(center bottom, #fffcd1 0%, #fff9b1 40%, #fff9b1 100%); background: -webkit-linear-gradient(bottom, #fffcd1 0%, #fff9b1 40%, #fff9b1 100%); -moz-transition: all 0.4s ease-in-out; -webkit-transition: all 0.4s ease-in-out;}
.Nag .Sheet2 {z-index: 5; top: 0; right: 2px; bottom: -3px; left: 2px; -moz-transition: all 0.6s ease-in-out; -webkit-transition: all 0.6s ease-in-out;}
.Nag .Sheet3 {z-index: 4; top: 0; right: 5px; bottom: -5px; left: 5px; box-shadow: 0 1px 5px rgba(34,25,25,0.3); -moz-box-shadow: 0 1px 5px rgba(34,25,25,0.3); -webkit-box-shadow: 0 1px 5px rgba(34,25,25,0.3); -moz-transition: all 0.7s ease-in-out; -webkit-transition: all 0.7s ease-in-out;}
.Nag .UndoSheet {z-index: 4; top: -100px; right: 0; bottom: 100px; left: 0; display: none; font-size: 15px; background-color: #f2f0f0; -moz-transition: all 0.7s ease-in-out; -webkit-transition: all 0.7s ease-in-out;}

#UnauthCallout .Nag {left: 50%; width: 680px; margin-left: -340px; text-align: left; -moz-transition: all 0s; -webkit-transition: all 0s;}
#UnauthCallout .Sheet {padding: 17px 20px 13px;}
#UnauthCallout .Sheet p {float: left; font-weight: 300;}
#UnauthCallout .Sheet p strong {font-weight: bold;}
#UnauthCallout .Sheet div {float: right; margin-top: 6px;}
#UnauthCallout .Sheet div .Button {margin-left: 10px;}
#UnauthCallout .Nag.fixed {top: -2px;}

#CategoriesBarPage #UnauthCallout .Nag {position: absolute; top: 74px;}
#CategoriesBarPage #UnauthCallout .Nag.fixed {top: 29px;}

.TimelineNag .Sheet p {line-height: 21px;}
.TimelineNag .Nag .Sheet {background-color: #f5f5ff;}
.TimelineNag .Nag .Sheet1 {background: -moz-linear-gradient(center bottom, #fff 0%, #f5f5ff 40%, #f5f5ff 100%); background: -o-linear-gradient(center bottom, #fff 0%, #f5f5ff 40%, #f5f5ff 100%); background: -webkit-linear-gradient(bottom, #fff 0%, #f5f5ff 40%, #f5f5ff 100%);}
.TimelineNag #FBNagFavicon {float: left; margin: 2px 14px 0 -2px;}
.TimelineNag #FBNagDotted {position: absolute; right: 3px; bottom: 2px;}
#CategoriesBarPage #UnauthCallout.TimelineNag .Nag.fixed {position: absolute !important; -moz-transition: 300ms all ease-in; -webkit-transition: 300ms all ease-in;}



