#BoardTitle {width: 780px; margin: 0 auto 30px; text-align: center; text-shadow: 0 1px rgba(255,255,255,0.9);}
  #BoardTitle h1 {margin-top: 76px;}
  #BoardTitle h1 strong {position: relative;}
