css:
@charset "UTF-8";

/* RESET
--------------------------------------------------------------------------------------- */
html{background: #f7f5f5 url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/paper.jpg) repeat;}body,div,dl,dt,dd,ul,ol,li,h1,h2,h3,h4,h5,h6,pre,code,form,fieldset,legend,input,textarea,p,blockquote,th,td{margin: 0;padding: 0;}table{border-collapse:collapse;border-spacing: 0;}fieldset,img{border: 0;}address,caption,cite,code,dfn,em,strong,th,var{font-style:normal;font-weight:normal;}li{list-style:none;}caption,th{text-align:left;}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal;}q:before,q:after{content:'';}abbr,acronym {border: 0;font-variant:normal;}sup{vertical-align:text-top;}sub{vertical-align:text-bottom;overflow:hidden;}input,textarea,select{font-family:inherit;font-size:inherit;font-weight:inherit; resize: none;outline:none;}legend{color:#000;}
body {font-family:"helvetica neue",arial,sans-serif;font-size:10px;color:#211922;}table{font-size:inherit;}pre,code,kbd,samp,tt{font-family:monospace;line-height:100%;}em{font-style:italic;}strong{font-weight:bold;}



/* FOUNDATION
--------------------------------------------------------------------------------------- */
h1 {margin: 32px 0 11px; font-size: 3em;}
h2 {padding: 0 0 8px; font-size: 2.5em;}
h1, h2 {letter-spacing: -0.0125em; font-weight: bold;}
p {margin: 0 0 .8em; line-height: 1.35em;}
a {font-weight: bold; color: #221919; text-decoration: none; outline: none;}
a:hover {color: #cb2027; text-decoration: underline;}
textarea, input {padding: 5px; line-height: 1em; color: #8c7e7e; background: #fcf9f9; border: 1px solid; border-color: #ddd #e1dfdf #d1cdcd; box-shadow: inset 0 0 2px rgba(255,255,255,0.75); -moz-box-shadow: inset 0 0 2px rgba(255,255,255,0.75); -webkit-box-shadow: inset 0 0 2px rgba(255,255,255,0.75);}
textarea:focus, input:focus {background-color: #fff; box-shadow: inset 0 1px 1px rgba(34,29,29,0.1); -moz-box-shadow: inset 0 1px 1px rgba(34,29,29,0.1); -webkit-box-shadow: inset 0 1px 1px rgba(34,29,29,0.1);}



/* REUSABLE STYLES
--------------------------------------------------------------------------------------- */
.hidden {display: none !important;}
.highlighted {color: #211922; background-color: #ff0 !important;}
.error {font-size: 18px; font-weight: 300; color: #f00 !important;}
.clear {clear: both !important;}
.noFloat {float: none !important;}
.floatRight {float: right !important;}
.floatLeft {float: left !important;}
.noBorder {border-width: 0 !important;}
.noBorderTop {border-top-width: 0 !important;}
.noBorderRight {border-right-width: 0 !important;}
.noBorderBottom {border-bottom-width: 0 !important;}
.noBorderLeft {border-left-width: 0 !important;}
.noMargin {margin: 0 !important;}
.noMarginTop {margin-top: 0 !important;}
.noMarginRight {margin-right: 0 !important;}
.noMarginBottom {margin-bottom: 0 !important;}
.noMarginLeft {margin-left: 0 !important;}
.fixed {position: fixed !important;}
.invisible {visibility: hidden !important;}
.red {color: red !important;}
.noscroll {overflow: hidden !important;}
.noscroll.extraScroll {margin-right: 15px;} /* Extra space on the right for FireFox */
.clearfix:after {clear: both; display: block; height: 0; content: " "; visibility: hidden;}

/* Linear Gradient */
.lg {background-color: #f0eded; background: -moz-linear-gradient(top,#fffcfc, #f0eded); background: -o-linear-gradient(#fffcfc, #f0eded); background: -webkit-linear-gradient(#fffcfc, #f0eded); filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#fffcfc', endColorstr='#f0eded');}

.ImgLink {display: block; background-color: #fff;}
.ImgLink:active {background-color: #221919;}
.ImgLink img {display: block; margin: 0 auto;}
.ImgLink:hover img,
.ImgLink:active img {opacity: .9; filter: alpha(opacity=90);}

.serif {font-family: palatino, "palatino linotype", "book antiqua", serif;}

.colorlight,
.colorlight a {color: #ad9c9c;}
.colorless,
.colorless a {color: #8c7e7e;}
.colormuted,
.colormuted a {color: #524d4d;}



/* CONTAINERS
--------------------------------------------------------------------------------------- */
.LiquidContainer {margin: 0 auto;}
.FixedContainer {width: 852px; margin: 0 auto;}
.FixedContainer .StaticForm {margin-top: 96px;}

#wrapper {position: relative; width: auto; min-width: 933px; margin: 0 auto; border: none;}
.BoardLayout {opacity: 0;}

#ColumnContainer {position: relative; min-height: 500px; margin: 60px auto 0;}
#ContextBar + #ColumnContainer {margin-top: 15px;}

.WhiteContainer {position: relative; margin: 76px auto 32px; padding: 0 18px 10px; background-color: #fff; box-shadow: 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: 0 1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.4);}
.WhiteContainer h2 {padding: 20px 0 15px; font-size: 32px; line-height: 43px;}
.WhiteContainer:after {clear: both; display: block; height: 0; content: " "; visibility: hidden;}



/* COMPONENTS
--------------------------------------------------------------------------------------- */
.PageOverlay {position: fixed; z-index: 9997; top: 0; right: 0; bottom: 0; left: 0; background-color: #fff; opacity: .95; filter: alpha(opacity=95);}

.Button.Indicator {position: fixed; z-index: 4; display: block; text-align: center; font-size: 15px; border-width: 0; box-shadow: 0 0 #fff; -moz-box-shadow: 0 0 #fff; -webkit-box-shadow: 0 0 #fff;}
.Button.Indicator span {border-width: 0; box-shadow: 0 1px 3px rgba(34,25,25,0.5); -moz-box-shadow: 0 1px 3px rgba(34,25,25,0.5); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.5);}

#NewIndicator {top: 88px; left: -3px; width: 145px; padding: 10px; -moz-transition: left 1s ease-in-out; -webkit-transition: left 1s ease-in-out;}
#NewIndicator.Offscreen {left: -175px; -moz-transition-duration: 250ms; -webkit-transition-duration: 250ms;}
#ScrollToTop {right: 15px; bottom: -10px; width: 50px; padding: 20px 10px 30px; -moz-transition: bottom 250ms ease-in-out; -webkit-transition: bottom 250ms ease-in-out;}
#ScrollToTop.Offscreen {bottom: -100px; -moz-transition-duration: 250ms; -webkit-transition-duration: 250ms;}

#LoadingPins {display: none; margin-bottom: 15px;  padding: 15px 0; text-align: center; font-weight: bold; border: double rgba(34,25,25,0.14); border-width: 3px 0;}
#LoadingPins img {display: inline-block; margin-right: 6px;}
#LoadingPins span {font-size: 1.3em;}

.MoreGrid {clear: both; display: none; width: 237px; margin: 0 auto 16px; padding: .7em .825em; text-align: center; text-decoration: none; color: #221919;}
#MorePeople {display: block; margin-top: 10px;}

.noise {position: absolute; z-index: 1; top: 0; right: 0; bottom: 0; left: 0; background: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/noise.png) repeat; opacity: .75;}

#ContextBar {position: relative; min-height: 24px; margin: 59px 0 15px; padding: 10px 0 12px; text-shadow: 0 1px rgba(255,255,255,0.9); border: double rgba(34,25,25,0.14); border-width: 3px 0;}
  #ContextBar.fixed {position: fixed; top: 15px; left: 15px;}
  #ContextBar p {margin: 4px 0 0; padding: 0; font-size: 1.8em; line-height: 1em; font-weight: 300; color: #211922; border-width: 0;}
  #ContextBar p span {margin-left: 5px;}
  #ContextBar p span.hilight {font-size: 1em; font-weight: 600; float: none; vertical-align: 5%;}
  #ContextBar p span.selected {margin: 0; color: #cb2027;}
  #ContextBar p span.greyselected {margin: 0; color: #8c7e7e;}
  #ContextBar a {font-weight: 300; text-decoration: underline;}

/* Tipsy CSS */
.tipsy {padding: 5px; font-size: 11px; line-height: 13px; background-image: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/tipsy.gif); background-repeat: no-repeat; opacity: 0.9;}
  .tipsy-inner {max-width: 150px; padding: 5px 8px; text-align: center; color: #fff; background-color: #221919;}
  .tipsy-north {background-position: top center;}
  .tipsy-south {background-position: bottom center;}
  .tipsy-east {background-position: right center;}
  .tipsy-west {background-position: left center;}



/* PIN CELL
--------------------------------------------------------------------------------------- */
.pin {position: absolute; width: 192px; padding: 15px 15px 0; font-size: 11px; background-color: #fff; box-shadow: 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: 0 1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.4);}
.pin .PriceContainer {position: absolute; z-index: 2; top: -15px; left: -15px; width: 110px; height: 110px; overflow: hidden;}
.pin .price {position: absolute; z-index: 2; top: 19px; left: -28px; width: 125px; height: 22px; padding-top: 8px; text-align: center; font-size: 11px; color: #524d4d; background-color: #f2f0f0; overflow: hidden; -webkit-transform: rotate(-45deg); -moz-transform:  rotate(-45deg); -o-transform: rotate(-45deg); -ms-transform: rotate(-45deg);}
.pin .ImgLink {position: relative; }
.pin .ImgLink:hover {text-decoration: none;}
.pin .ImgLink img {max-width: 192px; opacity: 1; -webkit-transition: opacity 0.3s; }
.pin .ImgLink img.lazy { opacity: 0; }
.pin .PinImage { background-color: #f2f0f0; }
.pin .PinImageImg {min-height: 75px; background-color: #f2f0f0;}
.pin .video {position: absolute; z-index: 1; top: 50%; left: 50%; width: 50px; height: 50px; margin: -25px 0 0 -25px;}
.pin .description {margin: 10px 0 5px; overflow: hidden; word-wrap: break-word;}

.convo {margin: 0 -15px; padding: 10px 15px; background-color: #f2f0f0;}
.convo:after {clear: both; display: block; height: 0; content: " "; visibility: hidden;}
.convo p {margin: 0 0 0 36px; overflow: hidden; word-wrap: break-word;}
.convo .ImgLink {float: left; margin-right: 6px; width: 30px;}
.convo .ImgLink img {width: 30px; height: 30px;}
a.convo {display: block; padding: 12px 15px; text-align: center;}
a.convo:hover {background-color: #f2f0f0;}
a.convo:active {color: #221919; background-color: #e1dfdf;}

.attribution {margin-top: 10px; color: #ad9c9c;}
.attribution a {color: #8c7e7e;}
.attribution p {display: table-cell; max-width: 156px; height: 30px; vertical-align: middle;}
.attribution .NoImage {height: auto; margin: 0;}

.comments {margin: 0 -15px; padding: 0 15px; border-top: 1px solid #d9d4d4;}
.comments .comment {border-top: 1px solid #fcfafa; border-bottom: 1px solid #d9d4d4; overflow: hidden;}
.comments .comment:last-child {border-bottom-width: 0;}
.comments .comment p {width: 156px;}

.write {border-top: 1px solid #d9d4d4; box-shadow: inset 0 1px #fcfafa; -moz-box-shadow: inset 0 1px #fcfafa; -webkit-box-shadow: inset 0 1px #fcfafa;}
.write form {position: relative;}
.write textarea {width: 148px; height: 21px; padding: 4px 3px 3px; font-size: 11px; border: 1px solid #ccc;}
.write .tagmate-menu {left: 36px; right: 0; border-radius: 0; -moz-border-radius: 0; -webkit-border-radius: 0;}
.write .tagmate-menu-option {padding: 3px 8px;}
.write .tagmate-menu-option img {width: 26px; height: 26px; margin-right: 5px;}
.write .tagmate-menu-option span {max-width: 107px; margin-top: 6px;}
.write .tagmate-menu-option:first-child {padding-top: 5px; border-radius: 0; -moz-border-radius: 0; -webkit-border-radius: 0;}
.write .tagmate-menu-option:last-child {padding-bottom: 5px; border-radius: 0; -moz-border-radius: 0; -webkit-border-radius: 0;}
.write .Button {display: none; float: right; clear: both; margin-top: 6px;}



/* BOARD CELL
--------------------------------------------------------------------------------------- */
.pinBoard {width: 222px !important; padding: 0 !important;}
  .pinBoard h3 {padding: 9px 15px 0; font-size: 13px; font-weight: 300; overflow: hidden;}
  .BoardLayout .pinBoard h3 {height: 16px;}
  .pinBoard .curatedBy {padding: 9px 15px 0; overflow: hidden;}
    .pinBoard .curatedBy img {float: left; width: 18px; height: 18px; margin-right: 4px;}
    .pinBoard .curatedBy p {float: left; width: 170px; margin: 2px 0 0; font-size: 11px; white-space: nowrap; overflow: hidden;}
  .pinBoard .link {display: block; height: 200px; background: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/BoardBackground.png) 15px 9px no-repeat; padding: 9px 0 4px 15px; overflow: hidden;}
    .pinBoard .link img {float: left; width: 61px; height: 61px; margin: 0 4px 4px 0;}
    .pinBoard.domainPins .link {background: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/BoardBackground_forDomainPins.png) 15px 9px no-repeat; height: auto; margin-bottom: 8px;}
  .pinBoard .likesComments {margin: 0; padding: 0 15px 10px; background-color: #fff;}
  .pinBoard .followBoard {padding: 0 15px 11px; text-align: center;}
    .pinBoard .followBoard .Button {display: block; padding: 0.5em 0.825em;}
    .pinBoard .followBoard .BoardMeta {float: left; width: auto; margin-top: 8px; font-size: 11px;}

/* REARRANGE */
.reArrange {float: right; text-decoration: none !important;}
.pinBoard.inMotion {cursor: move; box-shadow: 0 1px 6px rgba(0,0,0,0.25); -moz-box-shadow: 0 1px 6px rgba(0,0,0,0.25); -webkit-box-shadow: 0 1px 6px rgba(0,0,0,0.25);}
.pinBoard.inMotion:hover {box-shadow: 0 1px 6px rgba(0,0,0,0.5); -moz-box-shadow: 0 1px 6px rgba(0,0,0,0.5); -webkit-box-shadow: 0 1px 6px rgba(0,0,0,0.5);}
.pinBoard.inMotion * {cursor: move;}

.sortable {display: block; margin-left: -15px !important; zoom: 1;}
.sortable li {float: left; width: 222px; margin: 0 0 15px 15px;}
#SortableHelper {display: none; margin: 15px 0; padding: 15px; text-align: right; background-color: #ffc; overflow: hidden; box-shadow: 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: 0 1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.4);}
#SortableText {float: left; font-size: 18px; font-weight: 300;}
#SortableButtons .button {position: relative; top: 3px; margin-left: 10px; font-size: 14px;}
#SortStatus {position: absolute; top: 13px; right: 150px; font-size: 18px; font-weight: 300; opacity: 0; filter: alpha(opacity=0);}



/* USER CELL
--------------------------------------------------------------------------------------- */
.user .ImgLink {display: block; height: 192px; background-color: #f2f0f0; overflow: hidden;}
.user .ImgLink img {display: block; width: 192px; margin: 0 auto;}
.user h2 {display: block; height: 18px; font-size: 18px; line-height: 1em; font-weight: 300; padding: 10px 0 5px; white-space: nowrap; overflow: hidden;}
.user .location {height: 17px; margin-bottom: 8px; font-size: 13px; white-space: nowrap; overflow: hidden;}
.user .Button {display: block; margin-bottom: 12px; padding: 0.5em 0.825em;}



/* CELL BUTTONS
--------------------------------------------------------------------------------------- */
.pin .actions {display: none; position: absolute; z-index: 3; top: 6px; right: 6px; left: 6px;}
.pin .actions div {position: absolute;}
.pin .left {left: 0;}
.pin .right {right: 0; }

.pin:hover .actions {display: block;}
.actions .WhiteButton.Button11 {position: relative; z-index: 1; padding: 5px 8px; border: 2px solid rgba(140,126,126,0.5); border-radius: 6px; -moz-border-radius: 6px; -webkit-border-radius: 6px; box-shadow: inset 0 1px rgba(255,255,255,0.35); -moz-box-shadow: inset 0 1px rgba(255,255,255,0.35); -webkit-box-shadow: inset 0 1px rgba(255,255,255,0.35);}
.actions .WhiteButton.Button11 span {border-width: 0; border-radius: 5px; -moz-border-radius: 5px; -webkit-border-radius: 5px;}
.actions .WhiteButton.Button11:hover span {box-shadow: inset 0 1px rgba(255,255,255,0.35), 0 1px 2px rgba(35,24,24,0.75); -moz-box-shadow: inset 0 1px rgba(255,255,255,0.35), 0 1px 2px rgba(35,24,24,0.75); -webkit-box-shadow: inset 0 1px rgba(255,255,255,0.35), 0 1px 2px rgba(35,24,24,0.75);}
.actions .WhiteButton.Button11:active span {box-shadow: inset 0 1px 2px rgba(34,25,25,0.25), 0 0 1px rgba(232,230,230,0.5); -moz-box-shadow: inset 0 1px 2px rgba(34,25,25,0.25), 0 0 1px rgba(232,230,230,0.5); -webkit-box-shadow: inset 0 1px 2px rgba(34,25,25,0.25), 0 0 1px rgba(232,230,230,0.5);}

.actions .WhiteButton.Button11 em {position: relative; top: 1px; left: -2px; display: inline-block; width: 10px; height: 10px; background-image: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/ActionIcons10.png);}
.actions .WhiteButton.Button11.disabled span {background: #f2f0f0; opacity: 1; filter: none;}
.actions .WhiteButton.Button11.disabled:hover span {box-shadow: inset 0 1px rgba(255,255,255,0.35); -moz-box-shadow: inset 0 1px rgba(255,255,255,0.35); -webkit-box-shadow: inset 0 1px rgba(255,255,255,0.35);}
.actions .WhiteButton.Button11.disabled:active span {box-shadow: inset 0 1px 2px rgba(34,25,25,0.25); -moz-box-shadow: inset 0 1px 2px rgba(34,25,25,0.25); -webkit-box-shadow: inset 0 1px 2px rgba(34,25,25,0.25);}

.actions .repin_link em {background-position: 0 0; width: 11px !important;}
.actions .repin_link:hover em {background-position: 0 -10px;}
.actions .repin_link:active em {background-position: 0 -20px;}
.actions .likebutton em {background-position: -11px 0;}
.actions .likebutton:hover em {background-position: -11px -10px;}
.actions .likebutton:active em {background-position: -11px -20px;}
.actions .comment em {background-position: -21px 0;}
.actions .comment:hover em {background-position: -21px -10px;}
.actions .comment:active em {background-position: -21px -20px;}
.actions .comment.disabled em {background-position: -21px -30px;}



