/* FLIP
--------------------------------------------------------------------------------------- */
#flipScroll {position: fixed; z-index: 9999; top: 0; right: 0; bottom: 0; left: 0; overflow-y: auto; -webkit-perspective: 1000;}

#flip {position: fixed; z-index: 2; padding: 15px;} /* Also has class .pin */
#flip.flipped {width: 400px; height: 598px !important; margin: -299px 0 0 -200px; background: #fcf9f9; box-shadow: 0 3px 8px rgba(34, 25, 25, 0.4);}
#flip.flipped.transparent {top: 50% !important; left: 50% !important; background: none;}

#flip .face {position: absolute; top: 0; right: 0; bottom: 0; left: 0;}
#flip .back {background: #fcf9f9;}
#flip .front .PinImage {position: absolute; top: 0; left: 0; padding: 15px 15px 11px;}
#flip.flipped .face {width: 430px; height: 628px !important; padding: 0;}
#flip.flipped .front img {height: 628px !important; width: auto !important;}

/* 3D */
#flip.flipped,
#flip.reversed,
#flip .front img,
#flip .face {-webkit-transition: .3s all ease-in-out;}

#flip * {-webkit-backface-visibility: hidden;}

#flip {-webkit-transform-style: preserve-3d;}

#flip.flipped,
#flip .back {-webkit-transform: rotateY(180deg);}

/* FORM */
#BackLoader {position: absolute; top: 50%; left: 50%; margin: -16px 0 0 -16px;}

#flip .PinForm {margin: 0; padding: 30px;}
#flip .PinForm .Buttons {overflow: hidden;}
#flip .PinImagePreview {max-width: 370px; max-height: 370px; margin-bottom: 20px; overflow: hidden; box-shadow: 0 1px 3px rgba(34, 25, 25, 0.4);}
#flip .PinImagePreview img {width: 370px; height: auto !important; display: block;}



