/* LEFT */
.CloseupLeft {position: fixed; top: 60px; left: 50%; width: 222px; margin-left: -425px;}
.CloseupLeft .WhiteContainer {position: static; margin: 0 0 15px;}
.CloseupLeft .WhiteContainer.domainPins {position: static; margin: 0 0 15px; float: left;}

#mapContainer {position: relative;}
#map {width: 201px; height: 201px; overflow: hidden; margin: 6px 10px 10px; background: #eee;}
#pinOnMap {position: absolute; z-index: 1; left: 50%; bottom: 90px; margin-left: -25px;}

/* RIGHT */
.CloseupRight {float: right; width: 614px; margin-top: 60px;}
.CloseupRight .WhiteContainer {margin-top: 0; padding: 0;}

#PinPinner {padding: 20px 30px; border-bottom: 1px solid #d1cdcd; overflow: hidden;}
#PinPinner #PinnerImage {float: left; margin-right: 9px; width: 50px;}
#PinPinner #PinnerImage img {display: block; width: 50px; height: 50px;}
#PinPinner #PinnerName {font-size: 23px; line-height: 1em; margin-bottom: 2px;}
#PinPinner #PinnerName a {color: #211922; text-decoration: none;}
#PinPinner #PinnerName a:hover {color: #211922; text-decoration: underline; background-color: transparent;}
#PinnerStats {display: block; margin: 5px 0 0; padding-left: 60px; font-size: 13px;}

#PinActionButtons {padding: 20px 30px 10px; overflow: hidden;}
#PinActionButtons ul {float: left; margin-right: 12px;}
#PinActionButtons li {display: inline;}
#PinActionButtons li a {float: left; margin-right: 6px;}
#PinActionButtons em {position: relative; top: 1px; display: inline-block; width: 12px; height: 13px; margin-right: 4px; background: url(/web/20120229231408im_/http://passets-cdn.pinterest.com/images/ActionIcons.png) 0 0 no-repeat;}
#PinActionButtons .like-button em,
#PinActionButtons .unlike-button em {background-position: -24px 0;}
#PinActionButtons .like-button:hover em,
#PinActionButtons .unlike-button:hover em {background-position: -24px -13px;}
#PinActionButtons .like-button:active em,
#PinActionButtons .unlike-button:active em {background-position: -24px -26px;}
#PinActionButtons .repin-button em {background-position: -12px 0;}
#PinActionButtons .repin-button:hover em {background-position: -12px -13px;}
#PinActionButtons .repin-button:active em {background-position: -12px -26px;}

#PinSource {float: right; margin: 8px 0 0; font-size: 13px; color: #8c7e7e;}

#SocialShare {position: fixed; left: 50%; top: 162px; width: 120px; margin-left: 441px;}
#SocialShare li {display: block; margin: 11px 0; overflow: hidden;}
#SocialShare .button {display: inline-block; padding: .4em .6em; font-size: 11px; font-weight: normal;}

#PinImageHolder {position: relative; margin: 20px 30px; background-color: #f2f0f0; overflow: hidden;}
#PinImageHolder img {display: block; max-width: 554px; margin: 0 auto;}
#PinImageHolder .buyable {position: absolute; z-index: 1; top: 38px; left: -54px; width: 220px; padding: 10px 0; font-size: 13px; text-align: center; background-color: #f2f0f0; overflow: hidden; -webkit-transform:rotate(-45deg); -moz-transform:rotate(-45deg); -o-transform:rotate(-45deg); -ms-transform:rotate(-45deg);}

#PinCaption {padding: 0 30px 20px; font-size: 13px; line-height: 1.5em; border-bottom-width: 0; word-wrap: break-word;}
  .pintag {color: #524d4d;}
  .pintag:after {content: " ";}
#PinOrigin {display: block; margin-top: 8px; margin-bottom: 0; line-height: 16px;}

.PinComments {margin-bottom: 5px;}
.PinComments p {line-height: 1.33em;}
.PinComments .comment {clear: both; padding: 5px 30px; overflow: hidden;}
.PinComments .comment p {display: block; margin: 0 58px 4px 58px; font-size: 1.3em; word-wrap: break-word;}
.PinComments .comment img {display: block; width: 50px;}
.PinComments .CommenterMeta {color: #211922;}
.PinComments .comment ul {margin: 0 0 4px 58px;}
.PinComments .comment li {display:inline; margin-right: 3px;}
.PinComments .comment li img {display: inline; width: 5px;}
.PinComments .CommenterImage {float: left; margin-right: 8px;}
.PinComments .CommenterImage img {width: 50px; height: 50px;}
.PinComments .RepinnerImage {float: left; margin-right: 8px;}
#PinLikes {padding-bottom: 7px;}
#PinLikes .CommenterImage {float: left; margin: 0 8px 8px 0;}
.PinComments .RepinnerImage img {width: 50px; height: 50px;}
.PinComments .CommenterName {text-decoration: none;}
.PinComments .DeleteComment {padding: 3px 6px; text-decoration: none;}

#PinAddComment {padding: 0 30px 15px;}
#PinAddComment .CommenterImage {float: left; width: 50px;}
#PinAddComment .InputContainer {margin-left: 58px; position: relative;}
#PinAddComment .tagmate-menu {right: 0; left: 0; font-size: 13px; border-radius: 0; -moz-border-radius: 0; -webkit-border-radius: 0;}
#PinAddComment #CloseupComment {width: 478px; height: 32px; padding: 8px; font-size: 13px;}
#PinAddCommentControls {overflow: hidden;}
#PinAddCommentControls .HelpText {float: left; width: 320px; margin: 13px 0 0 58px; font-size: 13px;}
#PinAddCommentControls .Button {float: right; margin-top: 8px;}

#loginToComment {padding: 12px; text-align: center; font-size: 13px;}

.PinActivity {clear: both; padding: 15px 30px 15px 88px; font-size: 13px; border-top: 1px solid #d1cdcd; overflow: hidden;}
.PinActivity h4 {float: left; width: 50px; margin-left: -58px; padding-top: 16px; font-weight: bold; text-align: right;}
.PinActivity .CommenterImage {float: left; margin-right: 8px;}
.PinActivity .CommenterImage img {display: block; width: 50px; height: 50px;}
.PinActivity .repin {float: left; width: 495px; margin-bottom: 8px; line-height: 14px; color: #8c7e7e;}
.PinActivity .repin p {float: left; margin: 0; padding-top: 14px;}
.PinActivity .repin_attr {display: block; padding: 16px 0 25px;}

.PinRepinStory {margin: 0 0 8px; overflow: hidden;}
.PinRepinStory span {display: block; padding-top: 16px;}

.PinMoreActivity {clear: both; margin-top: 16px;}

/* REPORT */
#ReportModal .option {margin-bottom: 5px;}
#ReportModal .option label {position: static; display: block; color: #2A1919; cursor: pointer;}
#ReportModal .option input[type=radio] {margin-top: 4px;}

#ReportModal .Hidden {display: none;}
#ReportModal .ReportSuccess {text-align: center; font-size: 18px;}

#ReportLabel {margin-bottom: 10px; font-size: 18px; font-weight: bold;}

