@charset "UTF-8";

/* RESET
--------------------------------------------------------------------------------------- */
html{background: #f7f5f5 url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/paper.jpg) repeat;}body,div,dl,dt,dd,ul,ol,li,h1,h2,h3,h4,h5,h6,pre,code,form,fieldset,legend,input,textarea,p,blockquote,th,td{margin: 0;padding: 0;}table{border-collapse:collapse;border-spacing: 0;}fieldset,img{border: 0;}address,caption,cite,code,dfn,em,strong,th,var{font-style:normal;font-weight:normal;}li{list-style:none;}caption,th{text-align:left;}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal;}q:before,q:after{content:'';}abbr,acronym {border: 0;font-variant:normal;}sup{vertical-align:text-top;}sub{vertical-align:text-bottom;overflow:hidden;}input,textarea,select{font-family:inherit;font-size:inherit;font-weight:inherit; resize: none;outline:none;}legend{color:#000;}
body {font-family:"helvetica neue",arial,sans-serif;font-size:10px;color:#211922;}table{font-size:inherit;}pre,code,kbd,samp,tt{font-family:monospace;line-height:100%;}em{font-style:italic;}strong{font-weight:bold;}



/* FOUNDATION
--------------------------------------------------------------------------------------- */
h1 {margin: 32px 0 11px; font-size: 3em;}
h2 {padding: 0 0 8px; font-size: 2.5em;}
h1, h2 {letter-spacing: -0.0125em; font-weight: bold;}
p {margin: 0 0 .8em; line-height: 1.35em;}
a {font-weight: bold; color: #221919; text-decoration: none; outline: none;}
a:hover {color: #cb2027; text-decoration: underline;}
textarea, input {padding: 5px; line-height: 1em; color: #8c7e7e; background: #fcf9f9; border: 1px solid; border-color: #ddd #e1dfdf #d1cdcd; box-shadow: inset 0 0 2px rgba(255,255,255,0.75); -moz-box-shadow: inset 0 0 2px rgba(255,255,255,0.75); -webkit-box-shadow: inset 0 0 2px rgba(255,255,255,0.75);}
textarea:focus, input:focus {background-color: #fff; box-shadow: inset 0 1px 1px rgba(34,29,29,0.1); -moz-box-shadow: inset 0 1px 1px rgba(34,29,29,0.1); -webkit-box-shadow: inset 0 1px 1px rgba(34,29,29,0.1);}



/* REUSABLE STYLES
--------------------------------------------------------------------------------------- */
.hidden {display: none !important;}
.inviso {display: none;}
.highlighted {color: #211922; background-color: #ff0 !important;}
.error {font-size: 18px; font-weight: 300; color: #f00 !important;}
.clear {clear: both !important;}
.noFloat {float: none !important;}
.noState:hover {background-color: transparent !important;}
.noState:active {color: #221919 !important;}
.floatRight {float: right !important;}
.floatLeft {float: left !important;}
.noBorder {border-width: 0 !important;}
.noBorderTop {border-top-width: 0 !important;}
.noBorderRight {border-right-width: 0 !important;}
.noBorderBottom {border-bottom-width: 0 !important;}
.noBorderLeft {border-left-width: 0 !important;}
.noMargin {margin: 0 !important;}
.noMarginTop {margin-top: 0 !important;}
.noMarginRight {margin-right: 0 !important;}
.noMarginBottom {margin-bottom: 0 !important;}
.noMarginLeft {margin-left: 0 !important;}
.fixed {position: fixed !important;}
.invisible {visibility: hidden !important;}
.red {color: red !important;}
.noscroll {overflow: hidden !important;}
.noscroll.extraScroll, .noscroll.extraScroll #CategoriesBar, .noscroll.extraScroll #Header {margin-right: 15px;} /* Extra space on the right for FireFox */
.noscroll.extraScroll#CategoriesBarPage #Header, .noscroll.extraScroll#profile #Header {margin-right: 0;} /* Header doesn't need margin when relative positioned */
.noscroll.hidefixed #CategoriesBar.fixed, .noscroll.hidefixed #Header {display: none;}
.noscroll.hidefixed#CategoriesBarPage #Header, .noscroll.hidefixed#profile #Header {display: block;}
.clearfix:after {clear: both; display: block; height: 0; content: " "; visibility: hidden;}

/* Linear Gradient */
.lg {background-color: #f0eded; background: -moz-linear-gradient(top,#fffcfc, #f0eded); background: -o-linear-gradient(#fffcfc, #f0eded); background: -webkit-linear-gradient(#fffcfc, #f0eded); filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#fffcfc', endColorstr='#f0eded');}

.ImgLink {display: block; background-color: #fff;}
.ImgLink:active {background-color: #221919;}
.ImgLink img {display: block; margin: 0 auto;}
.ImgLink:hover img,
.ImgLink:active img {opacity: .9; filter: alpha(opacity=90);}

.serif {font-family: palatino, "palatino linotype", "book antiqua", serif;}

.colorlight,
.colorlight a {color: #ad9c9c;}
.colorless,
.colorless a {color: #8c7e7e;}
.colormuted,
.colormuted a {color: #524d4d;}



/* CONTAINERS
--------------------------------------------------------------------------------------- */
.LiquidContainer {margin: 0 auto;}
.FixedContainer {width: 852px; margin: 0 auto;}
.FixedContainer .StaticForm {margin-top: 96px;}

#wrapper {position: relative; width: auto; min-width: 933px; margin: 0 auto; border: none;}
.BoardLayout { visibility: hidden; /*opacity: 0;*/}

#ColumnContainer {position: relative; min-height: 500px; margin: 60px auto 0;}
#ColumnContainer.empty { background: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/building.gif) no-repeat center; }
#ContextBar + #ColumnContainer {margin-top: 15px;}

.WhiteContainer {position: relative; margin: 76px auto 32px; padding: 0 18px 10px; background-color: #fff; box-shadow: 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: 0 1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.4);}
.WhiteContainer h2 {padding: 20px 0 15px; font-size: 32px; line-height: 43px;}
.WhiteContainer:after {clear: both; display: block; height: 0; content: " "; visibility: hidden;}



/* COMPONENTS
--------------------------------------------------------------------------------------- */
.PageOverlay {position: fixed; z-index: 9997; top: 0; right: 0; bottom: 0; left: 0; background-color: #fff; opacity: .95; filter: alpha(opacity=95);}

#NewIndicator {top: 88px; left: -3px; width: 145px; padding: 10px; -moz-transition: left 1s ease-in-out; -webkit-transition: left 1s ease-in-out;}
#NewIndicator.TopNag {top: 130px;}
#NewIndicator.Offscreen {left: -175px; -moz-transition-duration: 250ms; -webkit-transition-duration: 250ms;}
#ScrollToTop {right: 15px; bottom: -10px; width: 70px; padding: 20px 10px 30px; -moz-transition: bottom 250ms ease-in-out; -webkit-transition: bottom 250ms ease-in-out;}
#ScrollToTop.Offscreen {bottom: -100px; -moz-transition-duration: 250ms; -webkit-transition-duration: 250ms;}

#LoadingPins {display: none; margin-bottom: 15px;  padding: 15px 0; text-align: center; font-weight: bold; border: double rgba(34,25,25,0.14); border-width: 3px 0;}
#LoadingPins img {display: inline-block; margin-right: 6px;}
#LoadingPins span {font-size: 1.3em;}

.Button.MoreGrid {clear: both; display: none; width: 237px; margin: 0 auto 16px; padding: .7em .825em; text-align: center; text-decoration: none; color: #221919;}
#MorePeople {display: block; margin-top: 10px;}

.noise {position: absolute; z-index: 1; top: 0; right: 0; bottom: 0; left: 0; background: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/noise.png) repeat; opacity: .75;}

#ContextBar {position: relative; min-height: 24px; margin: 59px 0 15px; padding: 10px 0 12px; text-shadow: 0 1px rgba(255,255,255,0.9);}
  #ContextBar.SearchNag { margin-top: 18px; }
  #ContextBar.fixed {position: fixed; top: 15px; left: 15px;}
  #ContextBar p {margin: 4px 0 0; padding: 0; font-size: 1.8em; line-height: 1em; font-weight: 300; color: #211922; border-width: 0;}
  #ContextBar p span {margin-left: 5px;}
  #ContextBar p span.hilight {font-size: 1em; font-weight: 600; float: none; vertical-align: 5%;}
  #ContextBar p span.selected {margin: 0; color: #cb2027;}
  #ContextBar p span.greyselected {margin: 0; color: #8c7e7e;}
  #ContextBar a {font-weight: 300; text-decoration: underline;}

/* Tipsy CSS */
.tipsy {padding: 5px; font-size: 11px; line-height: 13px; background-image: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/tipsy.gif); background-repeat: no-repeat; opacity: 0.9;}
  .tipsy-inner {max-width: 150px; padding: 5px 8px; text-align: center; color: #fff; background-color: #221919;}
  .tipsy-north {background-position: top center;}
  .tipsy-south {background-position: bottom center;}
  .tipsy-east {background-position: right center;}
  .tipsy-west {background-position: left center;}


/* Pin Discovery Orientation */
/* Unused zoom styles
#DiscoverPage.zoomed { overflow: hidden; }
#DiscoverPage.zoomed .overlay { display: block; }
#DiscoverPage .overlay { display: none; position: fixed; top: 0; right: 0; bottom: 0; left: 0; background-color: #000; opacity: 0.05; filter: opacity(alpha=1); }
#DiscoverPage .pin.zoom { z-index: 100; -webkit-transition: all 0.2s; box-shadow: 0 4px 18px rgba(34,25,25,0.5); -moz-box-shadow: 0 4px 17px rgba(34,25,25,0.5); -webkit-box-shadow: 0 4px 18px rgba(34,25,25,0.5); }
#DiscoverPage .pin.zoom img { -webkit-transition: all 0.2s; }
*/
#DiscoverPage #ScrollToTop { display: none; }
#DiscoverPage #PinSelector { width: 656px; margin: 40px auto 30px auto; }
#DiscoverPage .instruct { font-size: 14px; color: #666; -webkit-font-smoothing: antialiased; }
#DiscoverPage .message { /*padding: 18px 0;*/ text-align: left; /*background: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/discover_graph.png) no-repeat right top;*/ width: 678px; }
#DiscoverPage .loading .container { border-radius: 3px; box-shadow: 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: 0 1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.4); }
#DiscoverPage .loading { -webkit-transition: bottom 0.6s; position: fixed; left: 0; right: 0; bottom: -150px; width: 100%; text-align: center; }
#DiscoverPage .loading .container { width: 350px; margin: 0 auto; padding: 20px; background: #fff; overflow: hidden; font-size: 20px; line-height: 40px;}
#DiscoverPage .loading img { float: left; }
#DiscoverPage .message h2 { -webkit-font-smoothing: antialiased; font-size: 32px; font-weight: 300; color: #2B2B2B; line-height: 42px; /*padding: 20px 0 10px 0;*/ margin: auto; }
#DiscoverPage.one_step .message h2 { text-align: center; }
#DiscoverPage .message .instruct { /*padding: 0 0 20px 0;*/ line-height: 20px; }
#DiscoverPage #ColumnContainer { position: relative; padding-bottom: 300px; margin-top: 30px; }
#DiscoverPage .page { position: relative; -webkit-transition: opacity 0.5s; opacity: 0; }
#DiscoverPage .pin { -webkit-transition: box-shadow 0.1s; display: block; }
#DiscoverPage .pin img { display: block; }

#DiscoverPage .pin .edge,
#DiscoverPage .pin .edge div,
#DiscoverPage .pin .heart,
#DiscoverPage .pin .heart div,
#DiscoverPage .pin .heart span { -webkit-transition: all 0.2s; -moz-transition: all 0.2s; -ms-transition: all 0.2s; }

#DiscoverPage .pin .edge { display: none; width: 50px; height: 50px; overflow: hidden; position: absolute; top: -5px; right: -5px; }
.csstransforms3d #DiscoverPage .pin .edge { display: block; -webkit-transform: rotateZ(-45deg); -moz-transform: rotateZ(-45deg); -ms-transform: rotateZ(-45deg); }
.csstransforms3d #DiscoverPage .pin .edge div { width: 50px; height: 50px; position: absolute; top: -1px; left: 60px; background: #f5f5f5; box-shadow: 0 0 0px rgba(0,0,0,0.0); -moz-box-shadow: 0 0 0 rgba(0, 0, 0, 0); -webkit-transform: rotateZ(45deg); -moz-transform: rotateZ(45deg); -ms-transform: rotateZ(45deg); }

#DiscoverPage .pin .heart { display: none; width: 32px; height: 32px; overflow: hidden; position: absolute; right: 0px; top: 0px; }
.csstransforms3d #DiscoverPage .pin .heart { display: block; width: 50px; height: 50px; right: -40px; top: -40px; box-shadow:-2px 0px 1px rgba(0, 0, 0, 0.0); -webkit-transform: rotateZ(-45deg); -moz-transform: rotateZ(-45deg); -ms-transform: rotateZ(-45deg); }
.csstransforms3d #DiscoverPage .pin .heart div { width: 50px; height: 50px; -webkit-transform: rotateZ(45deg); -moz-transform: rotateZ(45deg); -ms-transform: rotateZ(45deg); }
.csstransforms3d #DiscoverPage .pin .heart div span { width: 50px; height: 50px; position: absolute; top: 45px; right: 45px; background: #fff; }

.csstransforms3d #DiscoverPage .pin:hover .edge { top: 4px; right: 4px; }
.csstransforms3d #DiscoverPage .pin:hover .edge div { left: 44px; box-shadow: 0 0 4px rgba(0,0,0,0.5); -moz-box-shadow: 0 0 4px rgba(0, 0, 0, 0.5); }
.csstransforms3d #DiscoverPage .pin:hover .heart { box-shadow: -2px 0px 1px rgba(0, 0, 0, 0.01); top: -31px; right: -31px; }
.csstransforms3d #DiscoverPage .pin:hover .heart div span { top: 35px; right: 35px; background: #fff; }

#DiscoverPage .pin.like .heart { display: block; background: #fff url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/heart.png) no-repeat 0 5px; }
.csstransforms3d #DiscoverPage .pin.like .heart { box-shadow: -2px 0px 1px rgba(0, 0, 0, 0.1); -moz-box-shadow: -2px 0 0px rgba(0, 0, 0, 0.05); top: -20px; right: -20px; background: none; }
.csstransforms3d #DiscoverPage .pin.like .heart div span { top: 25px; right: 24px; background: #fff url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/heart.png) no-repeat 25px 0; }
.csstransforms3d #DiscoverPage .pin.like .edge { top: 15px; right: 15px; }
.csstransforms3d #DiscoverPage .pin.like .edge div { left: 36px; box-shadow: 0 0 10px rgba(0,0,0,0.5); -moz-box-shadow: 0 0 6px rgba(0, 0, 0, 0.5); }

#DiscoverPage .pin:hover { cursor: pointer; box-shadow: 0 2px 4px rgba(34,25,25,0.4); -moz-box-shadow: 0 2px 4px rgba(34,25,25,0.4); -webkit-box-shadow: 0 2px 4px rgba(34,25,25,0.4); }
#DiscoverPage .FooterBar { -webkit-transition: all 0.5s; -moz-transition: all 0.5s; position: fixed; bottom: 0; left: 0; width: 100%; padding: 12px 0px; background-color:#FAF7F7; text-align:center; box-shadow: 0 -1px 3px rgba(34,25,25,0.4); -moz-box-shadow: 0 -1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: 0 -1px 3px rgba(34,25,25,0.4); }
#DiscoverPage .FooterBar .container { width: 645px; margin: 0 auto; }
#DiscoverPage .FooterBar .progress { margin: 8px 0; text-align: center; }
#DiscoverPage .FooterBar .progress span { -webkit-transition: all 0.5s; -moz-transition: all 0.5s; display: inline-block; margin-right: 15px; width: 17px; height: 19px; background: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/heart_gray.png); }
#DiscoverPage .FooterBar .progress span:last-of-type { margin: 0; }
#DiscoverPage .FooterBar .progress span.active { background-image: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/heart_red.png); }
#DiscoverPage .FooterBar .complete { display: none;}
#DiscoverPage .FooterBar .complete { text-align: left; font-size: 14px; color: #666; }
#DiscoverPage .FooterBar .complete strong { font-size: 21px; font-weight: 300; color: #2B2B2B; line-height: 30px;}
#DiscoverPage .FooterBar .complete p { float: left; margin-top: 4px; }
#DiscoverPage .FooterBar .complete .Button { float: right; }

#DiscoverPage.loading .loading { bottom: 130px; }
#DiscoverPage.ready.loading .loading { bottom: 160px; }
#DiscoverPage.ready .FooterBar { padding: 45px 0; }
#DiscoverPage.ready .FooterBar .complete{ display: block; }
#DiscoverPage.ready .FooterBar .progress { display: none; }

#DiscoverPage.done #ColumnContainer { display: none; }
#DiscoverPage.done .FooterBar { bottom: -150px; }
#DiscoverPage.done #PinSelector .loading { display: block; }
#DiscoverPage.done .message { opacity: 0; }
#DiscoverPage.done .loading { bottom: 45%; }


/* PIN CELL
--------------------------------------------------------------------------------------- */
.pin {position: absolute; width: 192px; padding: 15px 15px 0; font-size: 11px; background-color: #fff; box-shadow: 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: 0 1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.4);}
.pin .PinHolder {position: relative;}
.pin .PriceContainer {position: absolute; z-index: 2; top: -15px; left: -15px; width: 110px; height: 110px; overflow: hidden;}
.pin .price {position: absolute; z-index: 2; top: 19px; left: -28px; width: 125px; height: 22px; padding-top: 8px; text-align: center; font-size: 11px; color: #524d4d; background-color: #f2f0f0; overflow: hidden; -webkit-transform: rotate(-45deg); -moz-transform:  rotate(-45deg); -o-transform: rotate(-45deg); -ms-transform: rotate(-45deg);}
.pin .ImgLink {position: relative; }
.pin .ImgLink:hover {text-decoration: none;}
.pin .ImgLink img {max-width: 192px; opacity: 1; -webkit-transition: opacity 0.3s; }
.pin .ImgLink img.lazy { opacity: 0; }
.pin .PinImage { background-color: #f2f0f0; }
.pin .PinImageImg {min-height: 75px; background-color: #f2f0f0;}
.pin .video {position: absolute; z-index: 1; top: 50%; left: 50%; width: 50px; height: 50px; margin: -25px 0 0 -25px;}
.pin .description {margin: 10px 0 5px; overflow: hidden; word-wrap: break-word;}

/* Pin Activity */
.pin.activity { background: #fff; box-shadow: 0 1px 3px rgba(34,25,25,0.4); -moz-box-shadow: 0 1px 2px rgba(34,25,25,0.4); -webkit-box-shadow: 0 1px 3px rgba(34,25,25,0.4); position: absolute }
.pin.activity div.info { background-position: 15px 0px; background-repeat: no-repeat; border-bottom: 1px solid #ccc; padding: 0 15px 10px 35px; margin: 0 -15px 15px; }
.pin.activity .actions { top: auto; }
.pinBoard.activity { padding: 15px !important; width: 192px !important; }
.pinBoard.activity .board, .pinBoard.activity .holder { overflow: hidden; width: 191px; }
.pinBoard.activity .board a.link { height: 180px; width: 192px; }
.pinBoard.activity .cover { height: 130px; }
.pinBoard.activity .holder .cover img { width: 100%; }
.pinBoard.activity h3, .pinBoard.activity h4 { display: none; }
.pinBoard.activity .board { box-shadow: none; -moz-box-shadow: none; -webkit-box-shadow: none; }
.pinBoard.activity .board .followBoard { border: none; margin-top:10px; }
.pinBoard.activity .board .followBoard .Button { display: block; }
.pinBoard.activity .holder .thumbs img, .pinBoard.activity .holder .thumbs .empty { width: 47px; height: 47px; }
.user.activity a.ImgLink { width: 192px; display: block; margin-bottom: 10px; }
.user.activity .PersonIdentity { display: none; }
.pin .stats { overflow: hidden; }
.pin .stats span { float: left; margin-right: 10px; }
.pin.activity p.stats { margin: 0.8em 0; }
/* Pin          */ .pin.activity-1 div.info { background-image: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/Activity-Pin.png); }
/* Repin        */ .pin.activity-5 div.info { background-image: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/Activity-Repin.png); }
/* Like         */ .pin.activity-6 div.info { background-image: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/Activity-Like.png); }
/* Comment      */ .pin.activity-7 div.info { background-image: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/Activity-Comment.png); }
/* Follow Board */ .pin.activity-26 div.info { background-image: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/Activity-Board.png); }
/* Follow User  */ .pin.activity-45 div.info { background-image: url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/Activity-People.png); }

.pin.activity div.info p { padding: 5px 0 0 0; margin: 0; }
.pin.activity .UserImage, .pin.activity .BoardImage { overflow: hidden; margin-bottom: 15px; }

/* Simple pin */
.pin.simple { width: auto; padding: 5px; }


.convo {margin: 0 -15px; padding: 10px 15px; background-color: #f2f0f0;}
.convo:after {clear: both; display: block; height: 0; content: " "; visibility: hidden;}
.convo p {margin: 0 0 0 36px; overflow: hidden; word-wrap: break-word;}
.convo .ImgLink {float: left; margin-right: 6px; width: 30px;}
.convo .ImgLink img {width: 30px; height: 30px;}
a.convo {display: block; padding: 12px 15px; text-align: center;}
a.convo:hover {background-color: #f2f0f0;}
a.convo:active {color: #221919; background-color: #e1dfdf;}

.attribution {margin-top: 10px; color: #ad9c9c;}
.attribution a {color: #8c7e7e;}
.attribution p {display: table-cell; max-width: 156px; height: 30px; vertical-align: middle;}
.attribution .NoImage {height: auto; margin: 0;}

.AttributionSource {padding-top:1px; text-overflow: ellipsis; overflow: hidden; white-space: nowrap; padding-left: 21px; background: transparent url(/web/20120815015901im_/http://passets-cdn.pinterest.com/css/pinboard_64afdb23.css) 0 50% no-repeat;}
.AttributionSourceIs_behance {background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/behance.png);}
.AttributionSourceIs_etsy {background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/etsy.png);}
.AttributionSourceIs_flickr {padding-left: 20px; background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/flickr.png);}
.AttributionSourceIs_fivehundredpx {padding-left: 20px; background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/fivehundredpx.png);}
.AttributionSourceIs_geograph {padding-left: 20px; background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/geograph.png);}
.AttributionSourceIs_kickstarter {background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/kickstarter.png);}
.AttributionSourceIs_slideshare {background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/slideshare.png);}
.AttributionSourceIs_soundcloud {background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/soundcloud.png);}
.AttributionSourceIs_vimeo {background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/vimeo.png);}
.AttributionSourceIs_youtube {background-image:url(/web/20120815015901im_/http://passets-cdn.pinterest.com/images/attrib/youtube.png);}
.AttributionContainerInline {margin-top: 10px; margin-bottom:0;}
#AjaxFooter .AttributionContainerInline {margin-top: -10px; margin-bottom:20px;}

.comments {margin: 0 -15px; padding: 0 15px; border-top: 1px solid #d9d4d4;}
.comments .comment {border-top: 1px solid #fcfafa; border-bottom: 1px solid #d9d4d4; overflow: hidden;}
.comments .comment:last-child {border-bottom-width: 0;}
.comments .comment p {width: 156px;}
.ReportComment {text-shadow: none; opacity: .4; transition: opacity .3s; -moz-transition: opacity .3s; -webkit-transition: opacity .3s;}
.comment:hover .ReportComment {opacity: 1; font-weight:700;}

