button.Button::-moz-focus-inner {padding: 0; border: 0;}

.Button.Button11 {-moz-border-radius: 3px; -webkit-border-radius: 3px; border-radius: 3px; font-size: 11px; }
.Button.Button13 {-moz-border-radius: 4px; -webkit-border-radius: 4px; border-radius: 4px; font-size: 13px; }
.Button.Button18 {-moz-border-radius: 6px; -webkit-border-radius: 6px; border-radius: 6px; font-size: 18px; }
