.repinMask {position:fixed; top: 0; right: 0; bottom: 0; left: 0; overflow-y: auto; z-index: 9999; -webkit-transition: .4s background-color ease-in-out;}
.repinMask.visible {background: #fff; background-color: rgba(255,255,255,0.93);}

#flip, #flip .face, #flip .repinWrapper {-moz-transition: .4s -moz-transform ease-in-out; -webkit-transition: .4s -webkit-transform ease-in-out;}

#flip {position: absolute; z-index: 1; -moz-perspective: 1000px; -moz-transition-property: top, left, -moz-transform; -webkit-perspective: 1000px; -webkit-transition-property: top, left, -webkit-transform;}
#flip .face {position: absolute; -moz-backface-visibility: hidden; -moz-transform-style: preserve-3d; -webkit-backface-visibility: hidden; -webkit-transform-style: preserve-3d;}
#flip .front {-moz-transform: rotateY(0deg); -webkit-transform: rotateY(0deg);}
/* hide front face when flipped to fix scrolling issue in ff (started in ff 14) */
#flip.flipped .front {display: none; -moz-transform: rotateY(180deg); -webkit-transform: rotateY(180deg);}
#flip.flipping .front {display: block;}
#flip .back {-moz-transform: rotateY(-180deg); -webkit-transform: rotateY(-180deg);}
#flip.flipped .back {-moz-transform: rotateY(0deg); -webkit-transform: rotateY(0deg);}
/* webkit (chrome 21, safari 6) has issues with board selector
   when backface visibility set on repin wrapper.
   for them, only enable during flip. */
#flip .repinWrapper {-moz-backface-visibility: hidden;}
#flip.flipping .repinWrapper {-webkit-backface-visibility: hidden;}
#flip .back .repinWrapper {background-color: #f2f0f0; border: 1px solid #ad9c9c; box-shadow: 0 5px 30px rgba(34,25,25,0.4); -moz-box-shadow: 0 5px 30px rgba(34,25,25,0.4); -webkit-box-shadow: 0 5px 30px rgba(34,25,25,0.4);}
/* don't allow clicking on the pin clone */
#flip .front .faceMask {position: absolute; height: 100%; width: 100%; z-index: 9999;}

/* Some Content-Specific Tweaks */
#flip .front #zoom {background-color: #fff; width: 600px; padding: 20px 30px 0; font-size: 13px; box-shadow: 0 3px 8px rgba(34,25,25,0.4); -moz-box-shadow: 0 3px 8px rgba(34,25,25,0.4); -webkit-box-shadow: 0 3px 8px rgba(34,25,25,0.4);}
#flip .front #SocialShare, #flip .front #loading {display:none;}
#flip .front .WhiteContainer {margin: 0; padding: 0; width: 614px;}
#flip .front .actions {display: none;}
