    box-shadow: 0 1px 0 0 rgba(255, 255, 255, 0.34);
    color: #fff;
    cursor: default;
    font-weight: bold;
    padding: 9px 12px;
    position: absolute;
    right: 15px;
    top: 10px;
    text-shadow: 0 -1px rgba(0, 0, 0, 0.11);
    width: auto;
}

#formLoginButton.disabled {
    box-shadow: none;
    background: #ebebeb;
    border: 1px solid #ddd;
    color: #aaa;
}

#socialWrapper {
    border-bottom: 1px solid #dfdfdf;
    padding: 5px 3% 20px;
}

#interstitial {
    background-color: white;
    display: none;
}

#interImgWrapper {
    -moz-box-shadow: inset 0 -10px 15px rgba(0, 0, 0, 0.2);
    -webkit-box-shadow: inset 0 -10px 15px rgba(0,0,0,.2);
    box-shadow: inset 0 -10px 15px rgba(0,0,0,.2);
    max-height: 133px;
    min-height: 133px;
    overflow: hidden;
}

#interImg {
    opacity: .3;
    width: 100%;
}

#interBody {
    background-color: white;
    margin-bottom: 20px;
    padding-top: 20px;
}

#interMeta {
    margin-top: 15px;
}

#interHeader {
    font-size: 30px;
    font-weight: 600;
    margin: 0 6%;
}

#openInApp {
    background-color: #bc232c;
    -moz-border-radius: 8px;
    -webkit-border-radius: 8px;
    border-radius: 8px;
    color: white;
    font-size: 15px;
    font-weight: 500;
    margin: 20px auto;
    padding: 11px 0;
    text-align: center;
    width: 89%;
}

#stayInBrowserLink {
    color: #999;
    font-size: 12px;
    margin-top: 20px;
    text-align: center;
}


#appIcon {
    margin-left: 5%;
    width: 50px;
}

#appInfo {
    color: #999;
    display: inline-block;
    font-size: 12px;
    line-height: 11px;
    margin: 6px;
    vertical-align: top;
}

#appTitle {
    font-weight: bold;
}

#appStars {
    height: 10px;
    padding-top: 2px;
}

.spinner {
    background: #ebebeb;
    border-radius: 2px;
    display: none;
    height: 100%;
    left: 0;
    top: 0;
    position: absolute;
    width: 100%;
}

.spinner > div {
    background-image: url("http://web.archive.org/web/20150630235152im_/https://s-passets-cache-ak0.pinimg.com/webapp/style/app/common/images/spinner-small-2x.654fb1fe.gif");
    background-position: 0px 0px;
    background-size: 16px 16px;

    display: inline-block;
    opacity: 0.5;
    width: 16px;
    height: 16px;
    left: 50%;
    margin-left: -8px;
    margin-top: -8px;
    position: absolute;
    top: 50%;
}

.tooltip {
    background-color: white;
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    color: black;
    display: none;
    font-size: 11px;
    font-weight: normal;
    line-height: 150%;
    margin: 10px 10px 0;
    padding: 8px 14px;
    position: relative;
    text-align: left;
    width: 70%;
}

.tooltip > a {
    color: #717171;
}

.tooltip:before, .tooltip:after {
    border-color: white transparent;
    border-style: solid;
    content: '';
    display: block;
    left: 10px;
    position: absolute;
    right: 100%;
    width: 0;
}

#tooltip1:before, #tooltip1:after {
    border-width: 0 6px 8px;
    margin-top: -8px;
    top: 0;
}

#tooltip2:before, #tooltip2:after {
    border-width: 8px 6px 0;
    margin-bottom: -8px;
    bottom: 0;
}

@-webkit-keyframes scrollBgGrid {
    from { -webkit-transform: translateY(0px); }
    to { -webkit-transform: translateY(-300px); }
}

@-moz-keyframes scrollBgGrid {
    from { -moz-transform: translateY(0px); }
    to { -moz-transform: translateY(-300px); }
}
@-o-keyframes scrollBgGrid {
    from { transform: translateY(0px); }
    to { transform: translateY(-300px); }
}
@keyframes scrollBgGrid {
    from { transform: translateY(0px); }
    to { transform: translateY(-300px); }
}

@media screen and (orientation:portrait) and (max-device-height : 480px) {
    #interHeader {
        font-size: 27px;
        margin-bottom: 40px;
    }
    #interMeta {
        display: none;
    }
}

@media screen and (orientation:landscape) {
    #interImgWrapper {
        max-height: 71px;
        min-height: 71px;
    }

    #interBody {
        display: inline-block;
        width: 52%;
    }

    #interHeader {
        font-size: 19px;
    }

    #interFooter {
        display: inline-block;
        vertical-align: super;
        width: 47%;
    }

    #openInApp, #stayInBrowserLink {
        width: 90%;
    }

    #openInApp {
        margin: 0;
    }
}
