﻿/* ########## import ########## */
@import url(/web/20080718205439cs_/http://www.stripe.com/resource/css/global.css);
@import url(/web/20080718205439cs_/http://www.stripe.com/resource/css/navigation.css);
@import url(/web/20080718205439cs_/http://www.stripe.com/resource/css/title.css);


/* ########## general ########## */
body {
	font:					0.70em verdana,sans-serif;
	text-align:				center;
}

#body {
	color:					#888;
	margin:					20px auto;
	text-align:				left;
	width:					990px;
}

a {
	color:					#666;
	text-decoration:		underline;
}

h1, h2, h3, h4 {
	color:					#666;
	font-weight:			bold;
}

h1 {
	font-size:				160%;
}

h2 {
	font-size:				140%;
}

h3 {
	font-size:				120%;
	margin-top:				15px;
}

h4 {
	font-size:				100%;
}

p {
	margin-bottom:			5px;
}

.bold {
	font-weight:			bold !important;
}

.normal {
	font-weight:			normal !important;
}

.upper {
	text-transform:			uppercase;
}

.upper ul, ul.upper {
	list-style-type:		upper-alpha;
}

object.flash {
	display:				block;
}

object.flash div.no-flash {
	background:				#e8e8e8;
	border:					1px solid #888;
	color:					#888;
	display:				block;
	padding:				10px;
	margin-bottom:			5px;
}

.hide {
	display:				none;
}



/* ########## float fix ########## */
#header, 
#footer ul,
#navigation li#ni_search div, 
#home-pods, 
#text-advert, 
#title, 
.form div.field,
.form div.buttons,
#station-advert, 
div.station-item, 
div.station-item p.buttons,
form {
	*display:				inline-block;
}

#header:after,
#footer ul:after,
#navigation li#ni_search div:after, 
#home-pods:after, 
#text-advert:after, 
#title:after, 
.form div.field:after,
.form div.buttons:after,
#station-advert:after, 
div.station-item:after, 
div.station-item p.buttons:after,
form:after {
	content:				".";
	visibility:				hidden;
	display:				block;
	height:					0;
	clear:					both;
}

/* ########## png fix ########## */
#body #title a, #home-rotator li, #favourites a {
	behavior:				url(/web/20080718205439im_/http://www.stripe.com/resource/js/iepngfix/1_0_rc4/iepngfix.htc);
}

/* ########## loaders ########## */
.loader {
	background-repeat:		no-repeat;
	display:				block;
	z-index:				9999;
}

.loader.hide {
	display:				none;
}

.loader.snake {
	height:					16px;
	width:					16px;
}

#body.music div.loader.snake {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/loader/snake-white-red.gif);
	position:				absolute;
	right:					5px;
	top:					4px;
}


/* ########## header ########## */
#header {
	height:					35px;
	margin-bottom:			5px;
}

#header h1 {
	background:				#fff url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/logo.gif) no-repeat;
	cursor:					pointer;
	display:				block;
	float:					left;
	height:					35px;
	outline:				none;
	text-indent:			-9999px;
	width:					198px;
}

#header p, #header p a {
	color:					#636363;
	font-family:			helvetica,arial,sans-serif;
	font-weight:			bold;
	height:					35px;
	line-height:			35px;
}

#header p {
	float:					right;
	font-size:				90%;
	text-transform:			uppercase;
}

#header p a {
	text-decoration:		none;
}

#header p a:hover {
	color:					#19b5f1;
	text-decoration:		underline;
}


/* ########## home ########## */
#home-rotator-container {
	position:				relative;
}

#home-rotator-container div.loader {
	background:				#999 url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/loader/snake-white-grey.gif) no-repeat center center;
	border:					1px solid #fff;
	color:					#fff;
	height:					18px;
	left:					10px;
	position:				absolute;
	top:					10px;
	width:					18px;
}

/* rotator */
#home-rotator, #home-rotator li {
	background:				#e8e8e8;
	height:					285px;
	width:					990px;
}

#home-rotator {
	display:				block;
	margin-bottom:			5px;
	overflow:				hidden;
	position:				relative;
}

#home-rotator li {
	background-repeat:		no-repeat;
	position:				absolute;
	bottom:					-9999px;
}

#home-rotator li:first-child {
	position:				relative;
	bottom:					0;
}

#home-rotator li a {
	text-decoration:		none;
}

#home-rotator li a.title {
	display:				none;
}

#home-rotator li p {
	color:					#fff;
	font-size:				120%;
	height:					140px;
	position:				absolute;
	right:					48px;
	top:					85px;
	width:					350px;
}

#home-rotator li.what-you-want p {
	color:					#636363;
}

#home-rotator li.crystal-clear p {
	color:					#636363;
	right:					50px;
	top:					110px;
}

#home-rotator li.enjoy-stripe p {
	right:					50px;
}

#home-rotator li.music p {
	right:					50px;
	top:					110px;
}


/* rotator navigation */
#home-rotator-navigation {
	position:				absolute;
	top:					10px;
	right:					10px;
	z-index:				9999;
}

#home-rotator-navigation li {
	display:				inline;
}

#home-rotator-navigation li a {
	background:				#e8e8e8;
	border:					1px solid #fff;
	color:					#666;
	cursor:					pointer;
	display:				block;
	float:					left;
	font-size:				90%;
	height:					15px;
	line-height:			15px;
	margin:					0 2px;
	text-align:				center;
	text-decoration:		none;
	width:					15px;
}

#home-rotator-navigation li.selected a {
	background:				#999;
	border:					1px solid #fff;
	color:					#fff;
}

#home-rotator-navigation li a:hover {
	background:				#19b5f1;
	border:					1px solid #fff;
	color:					#fff;
}

/* pods */
#home-pods li {
	color:					#fff;
	cursor:					pointer;
	float:					left;
	height:					120px;
	position:				relative; 
	width:					325px;
}

#home-pods li h3 {
	display:				none;
}

#home-pods li p {
	font-size:				90%;
	height:					100px;
	margin-top:				5px;
	position:				absolute;
	right:					10px;
	text-align:				left;
	top:					25px;
	width:					210px;
}

#home-pods li.left {
	background:				#f90696 url(/web/20080718205439im_/http://www.stripe.com/resource/img/home/pod/demo.jpg) no-repeat;
	margin-left:			1px;
}

#home-pods li.center {
	background:				#9db312 url(/web/20080718205439im_/http://www.stripe.com/resource/img/home/pod/trial.jpg) no-repeat;
	margin:					0 6px;
}

#home-pods li.right {
	background:				#fea500 url(/web/20080718205439im_/http://www.stripe.com/resource/img/home/pod/guide.jpg) no-repeat;
}

/* ########## form ########## */
.form {
	background:				#636363;
	border-top:				1px solid #fff;
	padding:				20px 0;
}

.form .field .small {
	float:					right; 
	font-size:				85%;
	width:					205px;
}

.form fieldset {
	background:				#fff url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/form/corner-left.gif) no-repeat top left;
}

.form p.last {
	margin-bottom:			15px;
}

.form h4 {

	font-family:			arial;
	font-size:				140%;
	margin-bottom:			5px;
}

.form h5 {
	font-family:			arial;
	font-weight:			bold;
	margin-bottom:			5px;
}

.form h4, .form h5 {
	color:					#ea4498;
}

.form div.corner {
	background:				url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/form/corner-right.gif) no-repeat bottom right;
	padding:				10px 15px 5px;
}

.form div.field {
	margin-bottom:			5px;
}

.form div.field span {
	display:				none;
}

.form div.field.datetime span {
	display:				inline;
}

.form div.field.datetime select:last-child {
	margin
}

.form label {
	display:				block;
	float:					left;
	width:					150px;
}

.form input, .form select, .form textarea {
	color:					#333;
	font:					95% verdana,sans-serif;
	float:					left;
	text-indent:			3px;
	width:					200px;
}

 .form textarea {
	font:					95% verdana,sans-serif;
	height:					80px;
	*width:					195px;
 }

.form select {
	text-indent:			0;
	width:					204px;
}

.form div.buttons input {
	background-repeat:		no-repeat;
	background-color:		transparent;
	border:					0;
	color:					red;
	float:					right !important;
	height:					19px;
	margin-left:			5px;
	margin-top:				5px;
	text-indent:			-9999px;
	width:					auto;
}

.form div.buttons input.forgot-password {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/button/forgot-password.gif);
	width:					126px;
}

.form div.buttons input.log-out {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/button/logout.gif);
	width:					81px;
}

.form div.buttons input.reset {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/button/reset.gif);
	width:					67px;
}

.form div.buttons input.submit {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/button/submit.gif);
	width:					67px;
}

.form div.field table {
	margin-bottom:			5px;
}

.form div.field table label {
	margin:					0 5px;
}

.form div.field table label,
.form div.field table input {
	float:					none;
	display:				inline;
	width:					auto;
}

.form .message {
	background:				url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/form/warning-pink.gif) no-repeat 0 2px;
	color:					#666 !important;
	display:				block;
	font-weight:			bold;
	margin:					10px 0 0;
	padding-left:			30px;
	width:					320px;
}

.form .message div {
	padding-bottom:			20px;
}

.form .message ul {
	font-weight:			normal;
	list-style-type:		square;
	margin-left:			15px;
}

span.checkbox, span.radiobutton {
	display:				block !important;
}

span.checkbox input, span.radiobutton input, input.radiobutton {
	width:					auto;
}


/* custom forms */
.form fieldset.profile {
	width:					800px;	
	margin:					0 auto;
}

.form fieldset.profile .field .small {
	margin-right:			25px;
}

#body.activate .form fieldset,
#body.log-out .form fieldset,
fieldset.thank-you {
	width:					400px;	
	margin:					0 auto;
}

fieldset.requests {
	width:					390px;	
	margin:					0 auto;
}

#body.contact .form fieldset,
#body.send2friend .form fieldset {
	width:					385px;	
	margin:					0 auto;
}

fieldset.login {
	width:					385px;
	margin:					0 auto;
}

fieldset.sign-up {
	width:					385px;
	margin:					0 auto;
}

fieldset.log-out .message {
	margin-top:				0;
}

fieldset.log-out div.buttons {
	margin-bottom:			10px;
}

fieldset.profile div.buttons {
	margin:					10px 5px;
}

fieldset.requests div.buttons {
	margin-right:			5px;
}

/* buy */
#body.buy .form fieldset {
	width:					500px;
}

#body.buy div.option {
	margin-bottom:			10px;
}

#body.buy div.option label {
	color:					#ea4498;
	float:					none !important;
	font-family:			arial;
	font-weight:			bold;
}

#body.buy div.option input.radiobutton {
	margin-right:			5px;
}

#body.buy div.option input.promo-code, #body.buy div.option p {
	float:					none;
	margin-left:			18px;
}


/* aqua */
#body.activate .form h4, #body.activate .form h5 {
	color:					#00b1af;
}

#body.activate .form .message {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/form/warning-aqua.gif);
}

/* orange */
#body.contact .form h4, #body.contact .form h5,
#body.log-out .form h4, #body.log-out .form h5 {
	color:					#f89828;
}

#body.contact .form .message,
#body.log-out .form .message {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/form/warning-orange.gif);
}

/* purple */
#body.profile .form h4, #body.profile .form h5,
#body.requests .form h4, #body.requests .form h5,
.form .requests h4, .form .requests h5 {
	color:					#936fb1;
}

#body.profile .message,
#body.requests .message,
.form .requests .message {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/global/form/warning-purple.gif);
}



/* ########## text-advert ########## */
#text-advert {
	padding:				20px;
}

#text-advert div.text {
	float:					left;
	width:					560px;
}

#text-advert div.text p, #text-advert div.text li {
	text-align:				justify;
}

#text-advert div.advert {
	float:					right;
}

#text-advert div.advert img {
	margin-top:				25px;
}


/* ########## about us ########## */
#body.about #text-advert p {
	margin-bottom:			10px;
}

#body.about #text-advert .large {
	display:				block; 
	font-size:				20pt; 
	margin:					20px 0; 
	text-align:				center;
}


/* ########## faqs ########## */
#body.faq #text-advert li {
	border-bottom:			1px dashed #7c7c7c;
}

#body.faq #text-advert ul:last-child {
	border:					0;
}

#body.faq #text-advert dl {
	margin:					10px;
}

#body.faq #text-advert dt.selected {
	background:				#e5f6fd;
}

#body.faq #text-advert dd.selected {
	background:				#e5f6fd;
	margin-bottom:			5px;
	padding:				0 5px 5px !important;
}

#body.faq #text-advert dt {
	color:					#666;
	cursor:					pointer;
	font-weight:			bold;
	padding:				2px 5px 5px;
}


/* ########## terms/privacy ########## */
#body.terms #text-advert h3, #body.privacy #text-advert h3, #body.methods #text-advert h3  {
	font-size:				110%;
	text-transform:			uppercase;
}

#body.terms #text-advert h4, #body.privacy #text-advert h4, #body.methods #text-advert h4  {
	font-size:				100%;
	margin-top:				10px;
}

#body.terms #text-advert ol, #body.privacy #text-advert ol,
#body.terms #text-advert ul, #body.privacy #text-advert ul  {
	list-style-type:		lower-alpha;
	margin-bottom:			10px;
	margin-left:			40px;
}

#body.terms #text-advert ol.upper, #body.privacy #text-advert ol.upper{
	list-style-type:		upper-alpha;
}

#body.terms #text-advert ol ol, #body.privacy #text-advert ol ol {
	list-style-type:		lower-roman;
	margin-left:			20px;
	margin-top:				10px;
}

#body.terms #text-advert li, #body.privacy #text-advert li {
	margin-bottom:			5px;
}


/* ########## station ########## */
#station-advert {
	padding-top:			20px;
}

#station-advert p {
	padding-bottom:			5px;
}

#station-advert div.station {
	float:					left;
	width:					685px;
}

#station-advert div.advert {
	float:					right;
}

#station-advert div.advert img {
	display:				block;
}

div.station-item {
	border-bottom:			1px dashed #7c7c7c;
	margin-bottom:			20px;
	padding-bottom:			20px;
	position:				relative;
}

div.station-item:last-child {
	border-bottom:			0;
	margin-bottom:			0;
}

div.station-item div.logo {
	float:					left;
}

div.station-item div.logo img {
	margin-right:			5px;
	width:					150px;
}

div.station-item div.details {
	float:					right;
	width:					480px;
}

div.station-item div.details h4 {
	color:					#fff;
	display:				block;
	height:					25px;
	line-height:			25px;
	padding:				0 10px;
}

#body.music div.station-item div.details h4 {
	background:				#ef3e42;/*#ffa9ab;*/
}

#body.coming-soon div.station-item div.details h4 {
	background:				#f89828;
}

div.station-item div.details p {
	padding:				10px;
	text-align:				justify;
}

div.station-item div.details dl {
	margin:					0 10px 10px;
	visibility:				hidden;
}

div.station-item div.details dt {
	display:				inline;
	float:					left;
	font-weight:			bold;
	width:					100px;
}

div.station-item div.details dd {
	margin-left:			100px;
}

div.station-item a.add, div.station-item a.listen {
	background-repeat:		no-repeat;
	cursor:					pointer;
	display:				block;
	float:					left;
	text-indent:			-9999px;
}

div.station-item a.listen {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/button/listen-now.gif);
	height:					16px;
	margin-right:			25px;
	width:					90px;
}

div.station-item a.add {
	background-image:		url(/web/20080718205439im_/http://www.stripe.com/resource/img/button/add-to-favourites.gif);
	height:					14px;
	width:					125px;
}

#station-genre-list {
	color:					#fff;
	display:				none;
	font-weight:			bold;
	position:				absolute;
}

#station-genre-list select {
	color:					#999;
}

#body.music #station-genre-list {
	left:					200px;
	top:					8px;
}


/* ########## favourites ########## */
#favourites {
	margin:					0;
	padding:				0;
	position:				inherit;
	width:					375px;
}

div#favourites-message {
	background:				#ff0000;
	color:					#fff;
	padding:				5px 10px;
	margin-bottom:			5px;
	visibility:				hidden;
}

li.loading, li.no-results, li.sortable {
	background:				#fff url(/web/20080718205439im_/http://www.stripe.com/resource/img/my-stripe/favourites/background.jpg) no-repeat;
	cursor:					pointer;
	display:				block;
	height:					90px;
	list-style:				none;
	margin-bottom:			5px;
	position:				relative;
	text-align:				left;
	width:					375px;
}

li.loading, li.no-results {
	font-weight:			bold;
	padding:				10px;
}

li.sortable	a.up, li.sortable a.down {
	display:				none;
	height:					10px;
	left:					-15px;
	position:				absolute;
	width:					12px;
	z-index:				999;
}

li.sortable	a.up {
	background:				transparent url(/web/20080718205439im_/http://www.stripe.com/resource/img/my-stripe/favourites/arrow-up.gif) no-repeat;
	top:					2px;
}

li.sortable	a.down {
	background:				transparent url(/web/20080718205439im_/http://www.stripe.com/resource/img/my-stripe/favourites/arrow-down.gif) no-repeat;
	top:					12px;
}

li.sortable.move a.up, li.sortable.move a.down {
	display:				block;
}

li.sortable dl {
	display:				inline-block;
	margin:					0 auto;
	position:				absolute;
	top:					15px;
	left:					10px;
	width:					355px;
}

li.sortable dl:after {
	content:				".";
	visibility:				hidden;
	display:				block;
	height:					0;
	clear:					both;
}

li.sortable dt {
	background-color:		#fff;
	background-position:	center center;
	background-repeat:		no-repeat;
	display:				block;
	float:					left;
	height:					55px;
	outline:				none;
	text-indent:			-9999px;
	width:					165px;
}

li.sortable dd {
	margin-left:			175px;
}

li.sortable	dd a.listen, li.sortable dd a.remove {
	cursor:					pointer;
	display:				block;
	height:					16px;
	margin-top:				5px;
	outline:				none;
	text-indent:			-9999px;
}

li.sortable	dd a.listen {
	background:				transparent url(/web/20080718205439im_/http://www.stripe.com/resource/img/my-stripe/favourites/btn-listen.png) no-repeat;
	width:					115px;
