                }

                @-moz-keyframes flyoutAnimation {
                    0% {
                        opacity: 1.0;
                    }

                    66% {
                        opacity: 0.0;
                    }

                    100% {
                        opacity: 0.0;
                        -moz-transform: rotate(-25deg) translate(-400px, -220px);
                    }
                }


